mapper.select:

UPDATE P_MEMBER
SET PWD = #{pwd}
WHERE ID = #{id}


UPDATE P_MEMBER
SET NICK_NM = #{nick_nm}
WHERE ID = #{id}


SELECT COUNT(*)
FROM P_MEMBER 
WHERE NICK_NM = #{nick_nm}

mapper.delete:

DELETE FROM P_MEMBER 
WHERE ID = #{id}
AND PWD = #{pwd}

mapper.select:

SELECT * FROM P_BOARD
WHERE POST_CATE = '1'
AND POST_NUM = #{post_num}


SELECT * FROM P_BOARD
WHERE POST_CATE = '2'
AND POST_NUM = #{post_num}

	   select POST_NUM, POST_TITLE, ID, POST_DATE, VISITCOUNT
    from (
        select POST_NUM, POST_TITLE, ID, POST_DATE, VISITCOUNT,
            row_number() over(order by POST_NUM desc) as rNum
        from P_BOARD
        ) mb
   		 where rNum between #{rowStart} and #{rowEnd}
        order by POST_NUM desc
