mapper.select:

UPDATE P_MEMBER
SET PWD = #{pwd}
WHERE ID = #{id}


UPDATE P_MEMBER
SET NICK_NM = #{nick_nm}
WHERE ID = #{id}


SELECT COUNT(*)
FROM P_MEMBER 
WHERE NICK_NM = #{nick_nm}

mapper.delete:

DELETE FROM P_MEMBER 
WHERE ID = #{id}
AND PWD = #{pwd}

mapper.select:
select POST_NUM, POST_TITLE, POST_CONTENT, ID, POST_DATE, VISITCOUNT
from (
		select POST_NUM, POST_TITLE, POST_CONTENT, ID, POST_DATE,
		VISITCOUNT,
row_number() over(order by POST_NUM desc) as rNum
from P_BOARD
where id = #{id}

ORDER by 1
) mb
where rNum between #{rowStart} and #{rowEnd}
union
select POST_NUM, POST_TITLE, POST_CONTENT, ID, POST_DATE, VISITCOUNT
from (
	select POST_NUM, POST_TITLE, POST_CONTENT, ID, POST_DATE,
	VISITCOUNT,
	row_number() over(order by POST_NUM desc) as rNum
	from
	P_BOARD1
	where id = #{id}

	ORDER by 1
) mb
where rNum between #{rowStart} and #{rowEnd}
order by POST_NUM DESC
