			select *
   			 from (
        	select pt.*, row_number() over(order by label desc) as rNum
        	from P_TOURIST pt 
        	WHERE label LIKE '관광지' AND ALLTAG  NOT LIKE '%전시%'
        	) mb
   		 	where rNum between #{firstRow} and #{lastRow}


